import React, { useState } from 'react';
import axios from 'axios';

export default function SkinPredictor() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return;
    setLoading(true);
    setError(null);
    const fd = new FormData();
    fd.append('image', file);
    try {
      const res = await axios.post('/predict', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setResult(res.data);
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h3>Skin Disease Detector</h3>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit" disabled={loading}>{loading ? 'Predicting...' : 'Predict'}</button>
      </form>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {result && result.details && (
        <div style={{ marginTop: 12 }}>
          <h4>{result.details.disease}</h4>
          <p>{result.details.description}</p>
          <p><strong>Precautions:</strong> {result.details.precautions}</p>
          <p><strong>Cure:</strong> {result.details.cure}</p>
        </div>
      )}
    </div>
  );
}
