import React, { useEffect, useRef, useState } from "react";
export default function Chatbot(){
  const [open,setOpen]=useState(false);
  const [msgs,setMsgs]=useState([{from:"bot",text:"Hi! I can help with signup/login, camera/upload, history, and disease info."}]);
  const [input,setInput]=useState("");
  const bodyRef=useRef(null);
  useEffect(()=>{ if(bodyRef.current) bodyRef.current.scrollTop = bodyRef.current.scrollHeight; },[msgs,open]);
  const send = async () => {
    const text = input.trim();
    if(!text) return;
    setMsgs(m=>[...m,{from:"user",text}]); setInput("");
    try{
      const res = await fetch("/api/chat",{ method:"POST", headers:{ "Content-Type":"application/json" }, body: JSON.stringify({ message: text })});
      const data = await res.json();
      setMsgs(m=>[...m,{from:"bot",text:data.reply||"Hmm, not sure."}]);
    }catch{ setMsgs(m=>[...m,{from:"bot",text:"Sorry, server unreachable."}]); }
  };
  return (<>
    <button className="chat-trigger" title="Help" onClick={()=>setOpen(o=>!o)}>ðŸ’¬</button>
    {open && (
      <div className="chat-panel">
        <div className="chat-header">DermaScan Assistant</div>
        <div className="chat-body" ref={bodyRef}>
          {msgs.map((m,i)=>(<div key={i} className={"chat-msg "+(m.from==="user"?"user":"bot")}><div className="bubble">{m.text}</div></div>))}
        </div>
        <div className="chat-input">
          <input value={input} onChange={e=>setInput(e.target.value)} onKeyDown={e=>e.key==="Enter"&&send()} placeholder="Type a question..." />
          <button onClick={send}>Send</button>
        </div>
      </div>
    )}
  </>);
}
